import { createRouter, createWebHistory } from "vue-router";
const routes = [
  {
    path: "/",
    name: "home",
    components: {
      default: () => import("@/views/HomeView.vue"),
      newSideBar: () => import("@/components/newSideBar.vue"),
    },
    meta: {
      requiresAuth: true,
    },
  },
  // {
  //   path: "/about",
  //   name: "about",
  //   // route level code-splitting
  //   // this generates a separate chunk (about.[hash].js) for this route
  //   // which is lazy-loaded when the route is visited.
  //   component: () =>
  //     import(/* webpackChunkName: "about" */ "../views/AboutView.vue"),
  // },
  //LOGIN REGISTER API
  {
    path: "/apiLogin",
    name: "apiLogin",
    component: () => import("@/views/LoginC.vue"),
  },
  {
    path: "/apiRegister",
    name: "apiRegister",
    component: () => import("@/views/RegisterC.vue"),
  },
  {
    path: "/apiLogout",
    name: "apiLogout",
    component: () => import("@/views/LogOut.vue"),
  },
  //form wrong URL enter
  {
    path: "/:pathMatch(.*)*",
    name: "NotFound",
    component: () => import("@/views/NotFound.vue"),
  },
  //for forgot password
  {
    path: "/apiForgotPassword",
    name: "apiForgotPassword",
    component: () => import("@/views/ForgotPassword.vue"),
  },
  //for reset password
  {
    path: "/apiResetPassword/:token",
    name: "apiResetPassword",
    component: () => import("@/views/ResetPasswordView.vue"),
  },
  //for create new blog
  {
    path: "/apiAddNewBlog",
    named: "apiAddNewBlog",
    component: () => import("@/views/ResetPasswordView.vue"),
  },
  //for listing blog
  {},
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

// GLOBAL NAVIGATION GUARD
router.beforeEach((to) => {
  if (to.meta.requiresAuth && localStorage.getItem("token") == "") {
    console.log("'inn'");
    // return { name: "login", query: { redirect: to.fullPath } };
    return { name: "apiLogin" };
  }
});

export default router;
