import axios from "axios";
axios.defaults.baseURL = "http://127.0.0.1:8000/api";
axios.defaults.headers.common["Authorization"] =
  "Baarer " + localStorage.getItem("token");
axios.defaults.headers.get["Accepts"] = "application/json";
